mapper.select:
SELECT node.wId,node.nPoint,node.nDrag,node.nState,node.ntId,node.nPointTime 
FROM  node_type NATURAL JOIN node NATURAL JOIN words 
WHERE openId = #{openId}
AND ((TIMESTAMPDIFF(SECOND,nPointTime,#{time}))/100-nPoint)>0;

mapper.update:
update node
nPoint=#{nPoint}, nPointTime=#{nPointTime}, nDrag=#{nDrag},
where wId=#{wId} and openId=#{openId};
